  const updatechart = () => {
    let i = 0
    const container = document.querySelector('#container');
    container.addEventListener('click', function() {
    console.log(JSON.parse(container.dataset.positions)[i].quantity);
    charts.series[0].addPoint({
      name: JSON.parse(container.dataset.coins)[JSON.parse(container.dataset.positions)[i].coin_id - 1].title,
      y: JSON.parse(container.dataset.positions)[i].quantity * JSON.parse(container.dataset.coins)[JSON.parse(container.dataset.positions)[i].coin_id - 1].price
    });
  });
  i += 1;
}
