'use strict';
const api = () => {
const ccxt = require ('ccxt');
ccxt.use(function (req, res, next) {

          // Website you wish to allow to connect
          res.setHeader('Access-Control-Allow-Origin', 'http://localhost:3000');

          // Request methods you wish to allow
          res.setHeader('Access-Control-Allow-Methods', 'GET, POST, OPTIONS, PUT, PATCH, DELETE');

          // Request headers you wish to allow
          res.setHeader('Access-Control-Allow-Headers', 'X-Requested-With,content-type');

          // Set to true if you need the website to include cookies in the requests sent
          // to the API (e.g. in case you use sessions)
          res.setHeader('Access-Control-Allow-Credentials', true);

          // Pass to next layer of middleware
          next();
      });

let sum = 0;
let i = 0;

(async function () {
    const exchangeId = 'binance'
        , exchangeClass = ccxt[exchangeId]
        , exchange = new exchangeClass ({
            'apiKey': 'ySuKhuOVLAC0WExO0UkaEXJzpDSWUYtNU0r69I8cCf25pZm5N7NHUUGMNaMfq92m',
            'secret': 'cfkSnMUbzk2n7a3IvlpB2hlIMSLdVnbh8wsh14MDO8DHzcYBuQeDZEFCyB5CzRuY',
            'timeout': 30000,
            'enableRateLimit': true, // required! https://github.com/ccxt/ccxt/wiki/Manual#rate-limit
        })

    Object.entries((await exchange.fetchBalance()).total).forEach(item => {
      if (item[1] > 0) {
        const XMLHttpRequest = require("xmlhttprequest").XMLHttpRequest;

        const burl = "https://api.binance.com";

        let query = '/api/v1/ticker/24hr';

        query += `?symbol=${item[0]}USDT`;

        const url = burl + query;

        const ourRequest = new XMLHttpRequest();

        ourRequest.open('GET',url,true);

        ourRequest.onload = function(){
            const obj = JSON.parse(ourRequest.responseText);
            if (item[0] != 'USDT') {
              console.log("---------------------------------------------------");
              console.log(`${item[0]} : ${item[1]}`);
              console.log(`${item[0]} price : ${obj.lastPrice}$`);
              console.log(`Balance ${item[0]} : ${obj.lastPrice * item[1]}$`);
              sum += obj.lastPrice * item[1];
            } else {
              console.log("---------------------------------------------------");
              console.log(`${item[0]} : ${item[1]}`);
              console.log(`${item[0]} price : 1$`);
              console.log(`Balance ${item[0]} : ${item[1] * 1}$`);
              sum += 1 * item[1];
            }
            console.log(`Balance total usd : ${sum}$`);
        }
        ourRequest.send();
      }
    });
}) ();
}

export {api}
