const cors = () => {
  var cors = require('cors');
app.use(cors());
// to change your ports for different cors stuff:
app.set('port', process.env.PORT || 3000);
app.listen(app.get('port'), function() {
  console.log('we are listening on: ',
  app.get('port'))
});
}
export {cors}
